        public static List<string> GetStockName(int year)
        {
            var stockNames = "BHARTIARTL";
            if (year == 2022) stockNames = StockNames2022;
            else if (year == 2021) stockNames = StockNames2021;
            else if (year == 2020) stockNames = StockNames2020;
            else if (year == 2019) stockNames = StockNames2019;
            else if (year == 2018) stockNames = StockNames2018;
            else if(year == 2017) stockNames = StockNames2017;
            return stockNames.Split(',').ToList();



        }
    }
